<?phprequire_once(FRAME_WORK_PATH.'basic_classes/FieldSQL.php');require_once(FRAME_WORK_PATH.'Constants.php');require_once(FRAME_WORK_PATH.'basic_classes/ValidatorBool.php');class FieldSQLBool extends FieldSQL{	public function __construct($dbLink,$dbName, $tableName,					$id, $options=false) {		parent::__construct($dbLink,$dbName, $tableName,				$id, DT_BOOL,$options);	}    public function formatVal($val){		$formated = null;		FieldSQLBool::formatForDb($val,$formated);		return $formated;    }    public function getValueForDb(){		$val = parent::getValueForDb();		return $this->formatVal($val);    }    public function getOldValueForDb(){		$val = parent::getOldValueForDb();		return $this->formatVal($val);    }	    public static function formatForDb($valIn,&$valOut){		if ($valIn==='null'){			$valOut = 'null';					}		else{			$valOut = ($valIn===TRUE)? 'true':'false';					}				    }	}?>