<?phpclass FilterController {	private static function hash($contrId,$modelId){		return md5('contr_filter_'.$contrId.'_'.$modelId);	}	public static function set($contrId,$modelId,$filter){				$_SESSION[FilterController::hash($contrId,$modelId)]			= serialize($filter);	}	public static function delete($contrId,$modelId){		$hash = FilterController::hash($contrId,$modelId);		if (isset($_SESSION[$hash])){			unset($_SESSION[$hash]);		}			}	public static function get($contrName,$modelName){		$hash = FilterController::hash($contrName,$modelName);		if (isset($_SESSION[$hash])){			return unserialize($_SESSION[$hash]);		}		return NULL; 	}	}?>