<?phprequire_once(FRAME_WORK_PATH.'basic_classes/Model.php');class ModelFilterField extends Model{		private $conditionPattern;	private $logicOper;		public function __construct($conditionPattern="=",$logicOper="AND"){		parent::__construct();				$this->setConditionPattern($conditionPattern);		$this->setLogicOper($logicOper);				/*		$num_args = func_num_args();		for ($i=1;$i<$num_args;$i++){			$this->addField(func_get_arg($i));					}		*/		$this->insert();	}		public function setConditionPattern($conditionPattern){		$this->conditionPattern = $conditionPattern;	}	public function getConditionPattern(){		return $this->conditionPattern;	}	public function setLogicOper($logicOper){		$this->logicOper = $logicOper;	}	public function getLogicOper(){		return $this->logicOper;	}		public function toSQL(){		$cnt = $this->getFieldCount();		if (!$cnt){			return $this->getConditionPattern();		}		else{						args = array();			$fields = $this->getFieldIterator(); 			while($fields->valid()) {				array_push(args,$fields->current()->getValueForDb());				$fields->next();			}			return vsprintf($this->getConditionPattern(),args);		}			}	}?>