<?phpclass ModelGroupBySQL {		const SQL_STATEMENT = 'GROUP BY ';	public $fields=array();	/*	public function fieldExists($fieldId){		return array_key_exists($fieldId,$this->fields);	}	public function getFieldById($fieldId){		return $this->fields[$fieldId];	}	*/	public function addField($field){		$this->fields[$field->getId()] = $field;	}	public function getSQL(){		$res = '';		if (count($this->fields)){			foreach ($this->fields as $field){				$res.=($res=='')? '':',';				$res.=$field->getSQLNoAlias();			}			return ModelGroupBySQL::SQL_STATEMENT.$res;		}		return "";	}	}?>