<?phpclass ModelLimitSQL {		const SQL_STATEMENT_LIMIT = ' LIMIT %d';	const SQL_STATEMENT_OFFSET = ' LIMIT %d OFFSET %d';		private $from;	private $count;		public function __construct($count,$from=NULL){		$this->count = intval($count);		$this->from = ($from==NULL)? 0:intval($from);	}	public function getSQL(){		if ($this->from && $this->count){			return sprintf(ModelLimitSQL::SQL_STATEMENT_OFFSET,					$this->count,$this->from);		}		else if ($this->count){			return sprintf(ModelLimitSQL::SQL_STATEMENT_LIMIT,					$this->count);				}		return "";	}		public function getFrom(){		return $this->from;	}	public function setFrom($v){		$this->from = $v;	}	public function getCount(){		return $this->count;	}	public function setCount($v){		$this->count = $v;	}	}?>