<?phpclass ModelOrderSQL {		const SQL_STATEMENT = 'ORDER BY ';	const DEF_ORDER = 'ASC';	private $fields=array();		public function addField($field,$direct=NULL){		array_push($this->fields,array(				'field'=>$field,				'direct'=>($direct==NULL)?					ModelOrderSQL::DEF_ORDER:$direct)				);	}	public function getSQL(){		$res = '';		if (count($this->fields)){			foreach ($this->fields as $field){				$res.=($res=='')? '':',';				$res.=$field['field']->getId().' '.$field['direct'];				//$field['field']->getAlias()			}			$res = ModelOrderSQL::SQL_STATEMENT.$res;		}		return $res;	}	}?>