<?phprequire_once(FRAME_WORK_PATH.'basic_classes/ModelSQL.php');class ModelSQLDOCT extends ModelSQL{	public function __construct($dbLink,$options=NULL){		parent::__construct($dbLink,$options);	}	public function correct_keys(){		$this->getFieldById('login_id')->setValue($_SESSION['LOGIN_ID']);	}	public function select($insertMode=FALSE,					$modelWhere=NULL,					$modelOrder=NULL,					$modelLimit=NULL,					$fieldArray=NULL,					$grpFieldArray=NULL,					$aggFieldArray=NULL,					$calcTotalCount=NULL,					$toXML=NULL){		if (!isset($modelWhere)){			$modelWhere = new ModelWhereSQL();			$f = $this->getFieldById('login_id');			$f->setValue($_SESSION['LOGIN_ID']);											$modelWhere->addField($f);					}		else{			$ar = $modelWhere->getFieldsById('login_id','=');			if (is_array($ar)){				$ar[0]->setValue($_SESSION['LOGIN_ID']);			}			else{				$f = $this->getFieldById('login_id');				$f->setValue($_SESSION['LOGIN_ID']);												$modelWhere->addField($f);									}		}		parent::select($insertMode,				$modelWhere,				$modelOrder,				$modelLimit,				$fieldArray,				$grpFieldArray,				$aggFieldArray,				$calcTotalCount,				$toXML);	}		public function insert($needId=FALSE){		$this->correct_keys();		return parent::insert($needId);	}			public function delete(){		$this->correct_keys();		parent::delete();	}				public function update(){		//$this->correct_keys();		$this->getFieldById('login_id')->setOldValue($_SESSION['LOGIN_ID']);		parent::update();	}				}