<?phprequire_once(FRAME_WORK_PATH.'basic_classes/Validator.php');class ValidatorBool extends Validator {	public function validate($val){		$val = trim($val);		$l = strtolower($val);		if (!$val || !strlen($val) || $l=='undefined' || $l=='null'){			return 'null';		}					return ($val=='true' || $val=='yes' || $val=='1' || $val=='да');	}}?>